export async function decryptWithPrivateKey(encryptedKeyBase64, privateKeyPem) {
    try {
        const privateKey = await importPrivateKey(privateKeyPem);

        const encryptedKey = base64ToArrayBuffer(encryptedKeyBase64);

        const decryptedKey = await window.crypto.subtle.decrypt(
            { name: "RSA-OAEP" },
            privateKey,
            encryptedKey
        );

        // 4. Импортируем AES ключ
        return await window.crypto.subtle.importKey(
            "raw",
            decryptedKey,
            { name: "AES-GCM" },
            true,
            ["decrypt"]
        );
    } catch (error) {
        console.error('Decryption error:', error);
        throw error;
    }
}

export async function decryptFile(encryptedData, key) {
    try {
        // Разделяем IV (первые 12 байт) и зашифрованные данные
        const iv = encryptedData.slice(0, 12);
        const data = encryptedData.slice(12);

        return await window.crypto.subtle.decrypt(
            {
                name: "AES-GCM",
                iv: iv
            },
            key,
            data
        );
    } catch (error) {
        console.error('File decryption error:', error);
        throw error;
    }
}

async function importPrivateKey(pem) {
    // Удаляем PEM заголовки
    const pemHeader = "-----BEGIN PRIVATE KEY-----";
    const pemFooter = "-----END PRIVATE KEY-----";
    const pemContents = pem
        .replace(pemHeader, '')
        .replace(pemFooter, '')
        .replace(/\s+/g, '');

    // Декодируем Base64
    const binaryDer = base64ToArrayBuffer(pemContents);

    return await window.crypto.subtle.importKey(
        "pkcs8",
        binaryDer,
        { name: "RSA-OAEP", hash: "SHA-256" },
        true,
        ["decrypt"]
    );
}

export async function generateKeyPair() {
    return await window.crypto.subtle.generateKey(
        {
            name: "RSA-OAEP",
            modulusLength: 2048,
            publicExponent: new Uint8Array([0x01, 0x00, 0x01]),
            hash: "SHA-256",
        },
        true,
        ["encrypt", "decrypt"]
    );
}

export async function exportPublicKey(publicKey: CryptoKey) {
    const exported = await window.crypto.subtle.exportKey("spki", publicKey);
    return arrayBufferToBase64(exported);
}

export async function exportPrivateKey(privateKey: CryptoKey) {
    const exported = await window.crypto.subtle.exportKey("pkcs8", privateKey);
    const base64 = arrayBufferToBase64(exported);
    return `-----BEGIN PRIVATE KEY-----\n${base64}\n-----END PRIVATE KEY-----`;
}

export async function generateAesKey() {
    return await window.crypto.subtle.generateKey(
        {
            name: "AES-GCM",
            length: 256,
        },
        true,
        ["encrypt", "decrypt"]
    );
}

export async function encryptFile(file, key) {
    const fileData = await file.arrayBuffer();
    const iv = window.crypto.getRandomValues(new Uint8Array(12));

    const encryptedData = await window.crypto.subtle.encrypt(
        {
            name: "AES-GCM",
            iv: iv
        },
        key,
        fileData
    );

    // Объединяем IV и зашифрованные данные
    const result = new Uint8Array(iv.length + encryptedData.byteLength);
    result.set(iv, 0);
    result.set(new Uint8Array(encryptedData), iv.length);

    return new Blob([result], { type: file.type });
}

export async function encryptWithPublicKey(data, publicKeyPem) {
    // Импорт публичного ключа из PEM формата
    const publicKey = await importPublicKey(publicKeyPem);

    // Экспорт AES ключа
    const exportedKey = await window.crypto.subtle.exportKey("raw", data);

    // Шифрование
    const encrypted = await window.crypto.subtle.encrypt(
        { name: "RSA-OAEP" },
        publicKey,
        exportedKey
    );

    // Конвертация в Base64
    return arrayBufferToBase64(encrypted);
}

async function importPublicKey(pem) {
    // Удаляем PEM заголовки
    const pemHeader = "-----BEGIN PUBLIC KEY-----";
    const pemFooter = "-----END PUBLIC KEY-----";
    const pemContents = pem
        .replace(pemHeader, '')
        .replace(pemFooter, '')
        .replace(/\s+/g, '');

    // Декодируем Base64
    const binaryDer = base64ToArrayBuffer(pemContents);

    return await window.crypto.subtle.importKey(
        "spki",
        binaryDer,
        { name: "RSA-OAEP", hash: "SHA-256" },
        true,
        ["encrypt"]
    );
}

function arrayBufferToBase64(buffer) {
    let binary = '';
    const bytes = new Uint8Array(buffer);
    for (let i = 0; i < bytes.byteLength; i++) {
        binary += String.fromCharCode(bytes[i]);
    }
    return window.btoa(binary);
}

function base64ToArrayBuffer(base64) {
    const binaryString = window.atob(base64);
    const bytes = new Uint8Array(binaryString.length);
    for (let i = 0; i < binaryString.length; i++) {
        bytes[i] = binaryString.charCodeAt(i);
    }
    return bytes;
}
