import { useState, useEffect } from 'react';
import FileItem from '../components/FileItem';
import filesApi from "../api/files";
import {formatBytes, formatDate} from "../utils/helpers";
import {decryptFile} from "../utils/crypto";
import {saveAs} from "file-saver";
import {usePrivateKey} from "../context/PrivateKeyContext";

export default function SharedFiles() {
    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);
    const { privateKey } = usePrivateKey();

    useEffect(() => {
        const fetchFiles = async () => {
            try {
                const response = await filesApi.getAvailableFiles();
                setFiles(response.data.files);
            } catch (error) {
                console.error('Ошибка загрузки файлов:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchFiles();
    }, []);

    const handleDownload = async (file, encryptedAesKey, privateKeyPem) => {
        try {
            const response = await filesApi.downloadFile(file.id);
            console.log(response); // Это будет точно такой же response, как у axios.post

            console.log('Full response:', {
                status: response.status,
                headers: response.headers,
                data: response.data,
                dataType: typeof response.data,
                isArrayBuffer: response.data instanceof ArrayBuffer,
                constructor: response.data?.constructor?.name
            });

            if (!response.data || !(response.data instanceof ArrayBuffer)) {
                throw new Error(`Invalid data type: ${response.data?.constructor?.name}`);
            }

            // 3. Вызов decryptFile с правильными аргументами
            const decryptedBlob = await decryptFile(
                response.data, // ArrayBuffer
                encryptedAesKey, // string (base64)
                privateKeyPem // string (PEM)
            );

            saveAs(decryptedBlob, file.name);
        } catch (error) {
            console.error("Ошибка при скачивании файла:", error);
            throw error; // Пробрасываем ошибку для обработки выше
        }
    }

    if (loading) return <div>Загрузка...</div>;

    return (
        <div className="files-list">
            {files.length > 0 ? (
                files.map((file) => (
                    <FileItem
                        key={file.uuid}
                        file={{
                            id: file.uuid,
                            name: file.name,
                            size: formatBytes(file.size),
                            date: formatDate(file.created_at),
                            encryptedKey: file.symmetric_key
                        }}
                        isOwner={false}
                        onDownload={() => handleDownload({
                            id: file.uuid,
                            name: file.name,
                        }, file.symmetric_key, privateKey)}
                    />
                ))
            ) : (
                <p>Вам пока не предоставили доступ к файлам</p>
            )}
        </div>
    );
}
