import { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { useNavigate } from 'react-router-dom';
import { encryptFile, generateAesKey, exportPublicKey, encryptWithPublicKey } from '../../utils/crypto';
import filesApi from '../../api/files';

export default function UploadPrivateFile() {
    const [file, setFile] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const onDrop = useCallback((acceptedFiles) => {
        if (acceptedFiles.length) {
            setFile(acceptedFiles[0]);
            setError('');
        }
    }, []);

    const { getRootProps, getInputProps } = useDropzone({
        onDrop,
        maxFiles: 1,
        accept: {
            'application/pdf': ['.pdf'],
            'image/*': ['.png', '.jpg', '.jpeg'],
            'text/plain': ['.txt']
        }
    });

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!file) {
            setError('Please select a file');
            return;
        }

        setIsLoading(true);
        try {
            // 1. Генерация AES ключа
            const aesKey = await generateAesKey();

            // 2. Шифрование файла
            const encryptedFile = await encryptFile(file, aesKey);

            // 3. Получение публичного ключа из localStorage
            const publicKeyPem = localStorage.getItem('public_key');
            if (!publicKeyPem) throw new Error('Public key not found');

            // 4. Шифрование AES ключа публичным ключом
            const encryptedAesKey = await encryptWithPublicKey(
                aesKey,
                publicKeyPem
            );

            // 5. Конвертация в Base64
            const fileReader = new FileReader();
            fileReader.readAsDataURL(encryptedFile);

            fileReader.onload = async () => {
                const base64Data = fileReader.result.split(',')[1];

                // 6. Отправка на сервер
                await filesApi.createFile({
                    name: file.name,
                    symmetric_key: encryptedAesKey,
                    data_base64: base64Data
                });

                navigate('/storage/my');
            };

        } catch (err) {
            setError(err.message || 'File upload failed');
            console.error('Upload error:', err);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="upload-page">
            <div className="upload-container">
                <h2 className="upload-title">Upload New File</h2>

                <div
                    {...getRootProps()}
                    className={`dropzone ${isDragActive ? 'active' : ''} ${file ? 'has-file' : ''}`}
                >
                    <input {...getInputProps()} />
                    {file ? (
                        <div className="file-preview">
                            <div className="file-icon">
                                {file.type.startsWith('image/') ? (
                                    <i className="icon-image"></i>
                                ) : (
                                    <i className="icon-file"></i>
                                )}
                            </div>
                            <div className="file-info">
                                <p className="file-name">{file.name}</p>
                                <p className="file-size">{(file.size / 1024 / 1024).toFixed(2)} MB</p>
                            </div>
                            <button
                                type="button"
                                className="remove-btn"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    setFile(null);
                                }}
                            >
                                &times;
                            </button>
                        </div>
                    ) : (
                        <>
                            <i className="icon-upload"></i>
                            <p className="dropzone-text">
                                {isDragActive ? 'Drop the file here' : 'Drag & drop a file, or click to select'}
                            </p>
                            <p className="dropzone-hint">Supports: PDF, JPG, PNG, TXT (Max 50MB)</p>
                        </>
                    )}
                </div>

                {error && <div className="upload-error-message">{error}</div>}

                <form onSubmit={handleSubmit} className="upload-form">
                    <div className="form-group">
                        <label>Access Type:</label>
                        <div className="radio-group">
                            <label className="radio-option">
                                <input
                                    type="radio"
                                    name="accessType"
                                    value="private"
                                    checked={accessType === 'private'}
                                    onChange={() => setAccessType('private')}
                                />
                                <span className="radio-label">Private (only me)</span>
                            </label>

                            <label className="radio-option">
                                <input
                                    type="radio"
                                    name="accessType"
                                    value="public"
                                    checked={accessType === 'public'}
                                    onChange={() => setAccessType('public')}
                                />
                                <span className="radio-label">Public (anyone with link)</span>
                            </label>

                            <label className="radio-option">
                                <input
                                    type="radio"
                                    name="accessType"
                                    value="specific"
                                    checked={accessType === 'specific'}
                                    onChange={() => setAccessType('specific')}
                                />
                                <span className="radio-label">Specific person</span>
                            </label>
                        </div>
                    </div>

                    {accessType === 'specific' && (
                        <div className="form-group">
                            <label htmlFor="recipientEmail">Recipient Email:</label>
                            <input
                                type="email"
                                id="recipientEmail"
                                value={recipientEmail}
                                onChange={(e) => setRecipientEmail(e.target.value)}
                                placeholder="Enter recipient's email"
                                required
                            />
                        </div>
                    )}

                    <button
                        type="submit"
                        className="upload-btn"
                        disabled={!file || isLoading}
                    >
                        {isLoading ? (
                            <>
                                <span className="spinner"></span> Uploading...
                            </>
                        ) : (
                            'Upload File'
                        )}
                    </button>
                </form>
            </div>
        </div>
    );
}
