import {BrowserRouter as Router, Routes, Route, useLocation, Navigate} from 'react-router-dom';
import {AuthProvider, useAuth} from './context/AuthContext';
import SignIn from './components/Auth/SignIn';
import SignUp from './components/Auth/SignUp';
import FileStorage from './pages/FileStorage';
import MyFiles from './pages/MyFiles';
import SharedFiles from './pages/SharedFiles';
import UploadFile from './pages/UploadFile';
import React from "react";
import {PrivateKeyProvider} from "./context/PrivateKeyContext";

function ProtectedRoute({ children }) {
    const { isAuthenticated, loading } = useAuth();
    const location = useLocation();

    if (loading) {
        return <div>Loading...</div>;
    }

    if (!isAuthenticated) {
        return <Navigate to="/signin" state={{ from: location }} replace />;
    }

    return children;
}

function App() {
    return (
        <AuthProvider>
            <PrivateKeyProvider>
                <Router>
                    <Routes>
                        <Route path="/signin" element={<SignIn />} />
                        <Route path="/signup" element={<SignUp />} />

                        <Route path="/storage" element={<ProtectedRoute><FileStorage /></ProtectedRoute>}>
                            <Route path="my" element={<MyFiles />} />
                            <Route path="shared" element={<SharedFiles />} />
                        </Route>

                        <Route path="/upload" element={<ProtectedRoute><UploadFile /></ProtectedRoute>} />

                        <Route path="*" element={<SignIn />} />
                    </Routes>
                </Router>
            </PrivateKeyProvider>
        </AuthProvider>
    );
}

export default App;
