import { useState, useEffect } from 'react';
import filesApi from '../api/files';
import FileItem from '../components/FileItem';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { formatBytes, formatDate } from '../utils/helpers';
import { usePrivateKey } from "../context/PrivateKeyContext";
import {saveAs} from "file-saver";
import {decryptFile} from "../utils/crypto";
import axios from "axios";

export default function MyFiles() {
    const { isAuthenticated } = useAuth();
    const navigate = useNavigate();
    const { privateKey } = usePrivateKey();

    useEffect(() => {
        if (!isAuthenticated) {
            navigate("/signin");
        }
    }, [isAuthenticated, navigate]);

    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchFiles = async () => {
        try {
            const response = await filesApi.getMyFiles();
            setFiles(response.files);
        } catch (err) {
            setError(err.message || "Failed to load files");
            console.error("File loading error:", err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchFiles();
    }, []);

    const handleDownload = async (file, encryptedAesKey, privateKeyPem) => {
        try {
            const response = await filesApi.downloadFile(file.id)

            // 3. Вызов decryptFile с правильными аргументами
            const decryptedBlob = await decryptFile(
                response, // ArrayBuffer
                encryptedAesKey, // string (base64)
                privateKeyPem // string (PEM)
            );

            saveAs(decryptedBlob, file.name);
        } catch (error) {
            console.error("Ошибка при скачивании файла:", error);
            throw error; // Пробрасываем ошибку для обработки выше
        }
    }

    const handleDelete = async (fileUuid) => {
        try {
            await filesApi.deleteFile(fileUuid);
            await fetchFiles();
        } catch (err) {
            setError(err.message || "Failed to delete file");
            console.error("Delete error:", err);
        }
    };

    const handleShare = (fileUuid) => {
        console.log("Sharing file:", fileUuid);
    };

    if (loading) return <div className="loading">Loading files...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="files-list">
            {files.length > 0 ? (
                files.map((file) => (
                    <FileItem
                        key={file.uuid}
                        file={{
                            id: file.uuid,
                            name: file.name,
                            size: formatBytes(file.size),
                            date: formatDate(file.created_at),
                            encryptedKey: file.symmetric_key
                        }}
                        onDownload={() => handleDownload({
                            id: file.uuid,
                            name: file.name,
                        }, file.symmetric_key, privateKey)}
                        onShare={handleShare}
                        onDelete={handleDelete}
                        isOwner={true}
                    />
                ))
            ) : (
                <p className="empty-message">You don't have any files yet</p>
            )}
        </div>
    );
}
