import { useState, useEffect } from 'react';
import FileItem from '../components/FileItem';

export default function SharedFiles() {
    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Загрузка файлов, доступных пользователю
        const fetchFiles = async () => {
            try {
                // Здесь будет запрос к API
                const mockFiles = [
                    { id: 3, name: 'Общий документ.pdf', owner: 'user2@example.com', size: '3.1 MB', date: '2023-05-12' },
                ];
                setFiles(mockFiles);
            } catch (error) {
                console.error('Ошибка загрузки файлов:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchFiles();
    }, []);

    if (loading) return <div>Загрузка...</div>;

    return (
        <div className="files-list">
            {files.length > 0 ? (
                files.map((file) => (
                    <FileItem key={file.id} file={file} isOwner={false} />
                ))
            ) : (
                <p>Вам пока не предоставили доступ к файлам</p>
            )}
        </div>
    );
}
