import { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { generateKeyPair, exportPublicKey } from '../../utils/crypto';

export default function SignUp() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [keyPair, setKeyPair] = useState(null);
    const { signUp, loading } = useAuth();
    const navigate = useNavigate();

    const generateKeys = async () => {
        const pair = await generateKeyPair();
        setKeyPair(pair);
        return pair;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const keys = keyPair || (await generateKeys());
        const publicKey = await exportPublicKey(keys.publicKey);

        const { success } = await signUp(email, password, name, publicKey);
        if (success) {
            // Сохраняем приватный ключ локально
            localStorage.setItem('privateKey', JSON.stringify(keys.privateKey));
            navigate('/dashboard');
        }
    };

    return (
        <div className="auth-form">
            <h2>Sign Up</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Email</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Password</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" disabled={loading}>
                    {loading ? 'Creating account...' : 'Sign Up'}
                </button>
            </form>
        </div>
    );
}
