import { useState, useEffect } from 'react';
import filesApi from '../api/files';
import FileItem from '../components/FileItem';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { formatBytes, formatDate } from '../utils/helpers';
import { usePrivateKey } from "../context/PrivateKeyContext";

import { saveAs } from "file-saver";
import CryptoJS from "crypto-js";

export default function MyFiles() {
    const { isAuthenticated } = useAuth();
    const navigate = useNavigate();
    const { privateKey } = usePrivateKey();

    useEffect(() => {
        if (!isAuthenticated) {
            navigate("/signin");
        }
    }, [isAuthenticated, navigate]);

    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchFiles = async () => {
        try {
            const response = await filesApi.getMyFiles();
            setFiles(response.files);
        } catch (err) {
            setError(err.message || "Failed to load files");
            console.error("File loading error:", err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchFiles();
    }, []);

    const decryptRSA = async (encryptedKey) => {
        const privateKeyObj = await window.crypto.subtle.importKey(
            "pkcs8",
            new TextEncoder().encode(privateKey),
            { name: "RSA-OAEP", hash: "SHA-256" },
            true,
            ["decrypt"]
        );

        const decryptedKey = await window.crypto.subtle.decrypt(
            { name: "RSA-OAEP" },
            privateKeyObj,
            new Uint8Array(atob(encryptedKey).split("").map((c) => c.charCodeAt(0)))
        );
        return new TextDecoder().decode(decryptedKey);
    };

    const decryptAES = (encryptedData, key) => {
        const bytes = CryptoJS.AES.decrypt(encryptedData, key);
        return bytes.toString(CryptoJS.enc.Utf8);
    };

    const handleDownload = async (fileUuid, encryptedKey) => {
        if (!privateKey) {
            alert("Пожалуйста, загрузите приватный ключ");
            return;
        }
        try {
            const response = await filesApi.downloadFile(fileUuid);
            const { file, file_data } = response;

            const decryptedSymmetricKey = await decryptRSA(encryptedKey);
            const decryptedFileData = decryptAES(file_data, decryptedSymmetricKey);

            const blob = new Blob([decryptedFileData], { type: file.mime_type });
            saveAs(blob, file.name);
        } catch (err) {
            console.error("Download error:", err);
        }
    };

    const handleDelete = async (fileUuid) => {
        try {
            await filesApi.deleteFile(fileUuid);
            await fetchFiles();
        } catch (err) {
            setError(err.message || "Failed to delete file");
            console.error("Delete error:", err);
        }
    };

    const handleShare = (fileUuid) => {
        console.log("Sharing file:", fileUuid);
    };

    if (loading) return <div className="loading">Loading files...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="files-list">
            {files.length > 0 ? (
                files.map((file) => (
                    <FileItem
                        key={file.uuid}
                        file={{
                            id: file.uuid,
                            name: file.name,
                            size: formatBytes(file.size),
                            date: formatDate(file.created_at),
                            encryptedKey: file.symmetric_key
                        }}
                        onDownload={() => handleDownload(file.uuid, file.symmetric_key)}
                        onShare={handleShare}
                        onDelete={handleDelete}
                        isOwner={true}
                    />
                ))
            ) : (
                <p className="empty-message">You don't have any files yet</p>
            )}
        </div>
    );
}
