import client from './client';

export default {
    getMyFiles() {
        return client.get('/files', {
            headers: {
                'Content-Type': 'application/json',
            }
        });
    },
    getAvailableFiles() {
        return client.get('/available-files', {
            headers: {
                'Content-Type': 'application/json',
            }
        });
    },
    createFile(fileData) {
        return client.post('/file', formData, {
            headers: {
                'Content-Type': 'multipart/form-data',  // важно! Axios сам установит правильный boundary
            },
        });
    },
    shareFile(fileUUID, shareData) {
        return client.post(`/files/${fileUUID}`, shareData);
    },
    deleteFile(fileUUID) {
        return client.delete(`/files/${fileUUID}`);
    },
    downloadFile(fileUUID) {
        return client.post(`/download/files/${fileUUID}`);
    }
};
