import { useState, useEffect } from 'react';
import Spinner from './Spinner';
import UserSelector from './UserSelector';
import styles from './FileItem.module.css';
import filesApi from '../api/files';

export default function FileItem({
                                     file,
                                     onDownload,
                                     onShare,
                                     onDelete,
                                     isOwner,
                                     isDownloading
                                 }) {
    const [showShareModal, setShowShareModal] = useState(false);
    const [recipient, setRecipient] = useState(null);
    const [sharedUsers, setSharedUsers] = useState([]);
    const [loadingSharedUsers, setLoadingSharedUsers] = useState(false);
    const [shareError, setShareError] = useState(null);

    useEffect(() => {
        if (showShareModal && isOwner) {
            fetchSharedUsers();
        }
    }, [showShareModal]);

    const fetchSharedUsers = async () => {
        setLoadingSharedUsers(true);
        try {
            // const response = await filesApi.getSharedUsers(file.id);
            // setSharedUsers(response.data.users);
        } catch (error) {
            console.error("Failed to fetch shared users:", error);
        } finally {
            setLoadingSharedUsers(false);
        }
    };

    const handleShare = async (e) => {
        e.preventDefault();
        if (!recipient) return;

        setShareError(null);

        try {
            await onShare(file.id, recipient.email, recipient.publicKey, recipient.uuid);
            setRecipient(null);
            await fetchSharedUsers();
        } catch (error) {
            setShareError(error.message || "Failed to share file");
        }
    };

    const handleRevokeAccess = async (userId) => {
        try {
            await filesApi.revokeAccess(file.id, userId);
            await fetchSharedUsers();
        } catch (error) {
            console.error("Failed to revoke access:", error);
            setShareError("Failed to revoke access");
        }
    };

    return (
        <div className={styles.container}>
            <div className={styles.fileCard}>
                <div className={styles.fileInfo}>
                    <span className={styles.fileName}>{file.name}</span>
                    <div className={styles.fileMeta}>
                        <span className={styles.fileSize}>{file.size}</span>
                        <span className={styles.fileDate}>{file.date}</span>
                    </div>
                </div>

                <div className={styles.fileActions}>
                    <button
                        className={`${styles.button} ${styles.downloadButton}`}
                        onClick={() => onDownload(file.id)}
                        disabled={isDownloading}
                    >
                        {isDownloading ? (
                            <>
                                <Spinner size="small" color="white" />
                                Downloading...
                            </>
                        ) : 'Download'}
                    </button>

                    {isOwner && (
                        <>
                            <button
                                className={`${styles.button} ${styles.shareButton}`}
                                onClick={() => setShowShareModal(true)}
                            >
                                Share
                            </button>
                            <button
                                className={`${styles.button} ${styles.deleteButton}`}
                                onClick={() => onDelete(file.id)}
                            >
                                Delete
                            </button>
                        </>
                    )}
                </div>
            </div>

            {showShareModal && (
                <div className={styles.modalOverlay}>
                    <div className={styles.modalContent}>
                        <div className={styles.modalHeader}>
                            <h3 className={styles.modalTitle}>Share "{file.name}"</h3>
                            <button
                                className={styles.closeButton}
                                onClick={() => setShowShareModal(false)}
                            >
                                &times;
                            </button>
                        </div>

                        <form onSubmit={handleShare} className={styles.shareForm}>
                            <div className={styles.formGroup}>
                                <label className={styles.formLabel}>Select recipient:</label>
                                <div className={styles.recipientSelector}>
                                    <UserSelector
                                        onUserSelect={(user) => setRecipient(user)}
                                    />
                                </div>
                            </div>

                            <div className={styles.modalActions}>
                                <button
                                    type="button"
                                    className={styles.cancelButton}
                                    onClick={() => setShowShareModal(false)}
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className={styles.confirmButton}
                                    disabled={!recipient}
                                >
                                    Share
                                </button>
                            </div>
                        </form>

                        <div className={styles.sharedUsersSection}>
                            <h4 className={styles.sharedUsersTitle}>Users with access</h4>
                            {loadingSharedUsers ? (
                                <div className={styles.loadingIndicator}>
                                    <Spinner size="medium" />
                                </div>
                            ) : sharedUsers.length > 0 ? (
                                <ul className={styles.sharedUsersList}>
                                    {sharedUsers.map(user => (
                                        <li key={user.id} className={styles.sharedUserItem}>
                                            <span className={styles.sharedUserEmail}>{user.email}</span>
                                            <button
                                                onClick={() => handleRevokeAccess(user.id)}
                                                className={styles.revokeButton}
                                                title="Revoke access"
                                            >
                                                &times;
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p className={styles.noUsersMessage}>No users have access yet</p>
                            )}
                        </div>

                        {shareError && (
                            <div className={styles.errorMessage}>{shareError}</div>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
}
