import { useState, useEffect } from 'react';
import filesApi from '../api/files';
import FileItem from '../components/FileItem';
import { useAuth } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { formatBytes, formatDate } from '../utils/helpers';
import { usePrivateKey } from "../context/PrivateKeyContext";
import { saveAs } from "file-saver";
import {decryptFile, decryptWithPrivateKey, encryptFile, encryptWithPublicKey, generateAesKey} from "../utils/crypto";
import Spinner from "../components/Spinner";
import { toast } from 'react-toastify';

export default function MyFiles() {
    const { isAuthenticated } = useAuth();
    const navigate = useNavigate();
    const { privateKey } = usePrivateKey();

    useEffect(() => {
        if (!isAuthenticated) {
            navigate("/signin");
        }
    }, [isAuthenticated, navigate]);

    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [downloadingFile, setDownloadingFile] = useState(null);

    const fetchFiles = async () => {
        try {
            const response = await filesApi.getMyFiles();
            setFiles(response.data.files);
        } catch (err) {
            setError(err.message || "Failed to load files");
            console.error("File loading error:", err);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchFiles();
    }, []);

    const handleDownload = async (file, encryptedAesKey, privateKey) => {
        if (!privateKey) {
            toast.warn('Please upload your private key first to decrypt files');
            return;
        }

        console.log(privateKey)
        setDownloadingFile(file.id);
        try {
            const response = await filesApi.downloadFile(file.id);

            if (!response.data || !(response.data instanceof ArrayBuffer)) {
                throw new Error(`Invalid data type: ${response.data?.constructor?.name}`);
            }

            const decryptedBlob = await decryptFile(
                response.data,
                encryptedAesKey,
                privateKey
            );

            saveAs(decryptedBlob, file.name);
        } catch (error) {
            console.error("Download error:", error);
            setError("Failed to download file");
            toast.error('Failed to download file. Please try again.');
        } finally {
            setDownloadingFile(null);
        }
    }

    const handleDelete = async (fileUuid) => {
        try {
            await filesApi.deleteFile(fileUuid);
            await fetchFiles();
            toast.success('File deleted successfully');
        } catch (err) {
            setError(err.message || "Failed to delete file");
            console.error("Delete error:", err);
            toast.error('Failed to delete file');
        }
    };

    const handleShare = async (fileUuid, encryptedAesKey, recipientPublicKey, recipientUuid) => {
        console.log(encryptedAesKey, recipientPublicKey)
        const symmetricKey = await decryptWithPrivateKey(encryptedAesKey, privateKey);

        console.log(symmetricKey)

        encryptedAesKey = await encryptWithPublicKey(symmetricKey, recipientPublicKey);

        await filesApi.shareFile(fileUuid, {
            recipient_uuid: recipientUuid,
            symmetric_key: encryptedAesKey,
        });
    };

    if (loading) return (
        <div className="loading-container">
            <Spinner size="large" color="primary" />
            <p>Loading files...</p>
        </div>
    );

    if (error) return <div className="error-message">{error}</div>;

    return (
        <div className="files-list">
            {!privateKey && (
                <div className="key-warning">
                    <span>⚠️ You need to upload your private key to decrypt files</span>
                </div>
            )}

            {files.length > 0 ? (
                files.map((file) => (
                    <FileItem
                        key={file.uuid}
                        file={{
                            id: file.uuid,
                            name: file.name,
                            size: formatBytes(file.size),
                            date: formatDate(file.created_at),
                            encryptedKey: file.symmetric_key
                        }}
                        onDownload={() => handleDownload({
                            id: file.uuid,
                            name: file.name,
                        }, file.symmetric_key, privateKey)}
                        onShare={handleShare}
                        onDelete={handleDelete}
                        isOwner={true}
                        isDownloading={downloadingFile === file.uuid}
                        isDisabled={!privateKey}
                    />
                ))
            ) : (
                <p className="empty-message">You don't have any files yet</p>
            )}
        </div>
    );
}
