import { useState } from 'react';
import { Link, Outlet, useLocation, useNavigate } from 'react-router-dom';
import PrivateKeyUpload from '../components/PrivateKeyUpload';

export default function FileStorage() {
    const location = useLocation();
    const navigate = useNavigate();
    const [activeTab, setActiveTab] = useState(
        location.pathname.includes('shared') ? 'shared' : 'my'
    );

    const handleLogout = () => {
        // Здесь должна быть логика выхода (очистка токена, состояния и т.д.)
        // Например:
        localStorage.removeItem('authToken');
        // Перенаправляем на страницу входа
        navigate('/login');
    };

    return (
        <div className="file-storage-container">
            {/* Блок для загрузки приватного ключа */}
            <div className="key-upload-section">
                <PrivateKeyUpload />
            </div>

            {/* Кнопка выхода */}
            <button
                onClick={handleLogout}
                className="logout-btn"
                style={{
                    padding: '8px 16px',
                    background: '#f44336',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer'
                }}
            >
                Выйти
            </button>

            {/* Навигационные табы */}
            <div className="tabs-container">
                <Link
                    to="/storage/my"
                    className={`tab ${activeTab === 'my' ? 'active' : ''}`}
                    onClick={() => setActiveTab('my')}
                >
                    Мои файлы
                </Link>
                <Link
                    to="/storage/shared"
                    className={`tab ${activeTab === 'shared' ? 'active' : ''}`}
                    onClick={() => setActiveTab('shared')}
                >
                    Доступные файлы
                </Link>
            </div>

            <div className="add-file-btn-container">
                <Link to="/upload" className="add-file-btn">
                    + Добавить файл
                </Link>
            </div>

            {/* Контент страницы (будет меняться в зависимости от роута) */}
            <div className="files-content">
                <Outlet />
            </div>
        </div>
    );
}
