import { useState } from 'react';
import { Link, Outlet, useLocation, useNavigate } from 'react-router-dom';
import PrivateKeyUpload from '../components/PrivateKeyUpload';

export default function FileStorage() {
    const location = useLocation();
    const navigate = useNavigate();
    const [activeTab, setActiveTab] = useState(
        location.pathname.includes('shared') ? 'shared' : 'my'
    );

    const handleLogout = () => {
        localStorage.removeItem('access_token');
        navigate('/signin');
    };

    return (
        <div className="file-storage-container">
            <div className="file-storage-top">
                <h2>Хранилище файлов</h2>
                <button
                    onClick={handleLogout}
                    className="logout-btn"
                >
                    Выйти
                </button>
            </div>

            <div className="key-upload-section">
                <PrivateKeyUpload />
                <button
                    className="regenerate-keys-button"
                    onClick={async () => {
                        if (window.confirm('Are you sure you want to regenerate your keys? This will invalidate your current keys.')) {
                            try {
                                // Здесь должна быть логика перевыпуска ключей
                                await regenerateKeys();
                                alert('Keys regenerated successfully!');
                            } catch (error) {
                                alert('Failed to regenerate keys: ' + error.message);
                            }
                        }
                    }}
                >
                    Regenerate Keys
                </button>
            </div>

            <div className="tabs-container">
                <Link
                    to="/storage/my"
                    className={`tab ${activeTab === 'my' ? 'active' : ''}`}
                    onClick={() => setActiveTab('my')}
                >
                    Мои файлы
                </Link>
                <Link
                    to="/storage/shared"
                    className={`tab ${activeTab === 'shared' ? 'active' : ''}`}
                    onClick={() => setActiveTab('shared')}
                >
                    Доступные файлы
                </Link>
            </div>

            <div className="add-file-btn-container">
                <Link to="/upload" className="add-file-btn">
                    + Добавить файл
                </Link>
            </div>

            {/* Контент страницы */}
            <div className="files-content">
                <Outlet />
            </div>
        </div>
    );
}
