import { useState, useEffect } from 'react';
import filesApi from '../api/files';
import FileItem from '../components/FileItem';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { formatBytes, formatDate } from '../utils/helpers';

export default function MyFiles() {
    const { isAuthenticated } = useAuth();
    const navigate = useNavigate();

    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/signin');
        }
    }, [isAuthenticated, navigate]);

    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchFiles = async () => {
            try {
                const response = await filesApi.getMyFiles();
                setFiles(response.files);
            } catch (err) {
                setError(err.message || 'Failed to load files');
                console.error('File loading error:', err);
            } finally {
                setLoading(false);
            }
        };

        fetchFiles();
    }, []);

    const handleDownload = async (fileUuid) => {
        try {
            // Здесь будет логика скачивания файла
            console.log('Downloading file:', fileUuid);
        } catch (err) {
            console.error('Download error:', err);
        }
    };

    const handleShare = (fileUuid) => {
        // Здесь будет логика открытия модалки для шаринга
        console.log('Sharing file:', fileUuid);
    };

    if (loading) return <div className="loading">Loading files...</div>;
    if (error) return <div className="error">{error}</div>;

    return (
        <div className="files-list">
            {files.length > 0 ? (
                files.map((file) => (
                    <FileItem
                        key={file.uuid}
                        file={{
                            id: file.uuid,
                            name: file.name,
                            size: formatBytes(file.size),
                            date: formatDate(file.created_at),
                            encryptedKey: file.symmetric_key
                        }}
                        onDownload={handleDownload}
                        onShare={handleShare}
                        isOwner={true}
                    />
                ))
            ) : (
                <p className="empty-message">You don't have any files yet</p>
            )}
        </div>
    );
}
