import { decryptWithPrivateKey, decryptFile } from './crypto';
import filesApi from '../api/files';

export async function downloadAndDecryptFile(fileUuid, privateKeyPem) {
    try {
        console.log('downloadAndDecryptFile', fileUuid, privateKeyPem);
        const response = await filesApi.downloadFile(fileUuid);
        const { file, file_data: encryptedFileBase64 } = response;

        const encryptedFileData = base64ToArrayBuffer(encryptedFileBase64);

        const symmetricKey = await decryptWithPrivateKey(
            file.symmetric_key,
            privateKeyPem
        );

        const decryptedFile = await decryptFile(encryptedFileData, symmetricKey);

        const blob = new Blob([decryptedFile], { type: 'application/octet-stream' });
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = file.name;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);

        return true;
    } catch (error) {
        console.error('Download error:', error);
        throw error;
    }
}

function base64ToArrayBuffer(base64) {
    const binaryString = window.atob(base64);
    const bytes = new Uint8Array(binaryString.length);
    for (let i = 0; i < binaryString.length; i++) {
        bytes[i] = binaryString.charCodeAt(i);
    }
    return bytes;
}
