import {useCallback, useState} from 'react';
import {useDropzone} from 'react-dropzone';
import {useNavigate} from 'react-router-dom';
import {encryptFile, encryptWithPublicKey, generateAesKey,} from '../utils/crypto';
import filesApi from '../api/files';
import usersApi from '../api/users';
import UserSelector from "../components/UserSelector";

export default function UploadFile() {
    const [file, setFile] = useState(null);
    const [accessType, setAccessType] = useState('private');
    const [recipientEmail, setRecipientEmail] = useState('');
    const [recipientPublicKey, setRecipientPublicKey] = useState(null);
    const [recipientUuid, setRecipientUuid] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const [progress, setProgress] = useState(0);
    const navigate = useNavigate();

    const onDrop = useCallback((acceptedFiles) => {
        if (acceptedFiles.length) {
            const selectedFile = acceptedFiles[0];

            // Проверка размера файла (макс. 50MB)
            if (selectedFile.size > 50 * 1024 * 1024) {
                setError('File size exceeds 50MB limit');
                return;
            }

            setFile(selectedFile);
            setError('');
            setProgress(0);
        }
    }, []);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        maxFiles: 1,
        accept: {
            'application/pdf': ['.pdf'],
            'image/*': ['.png', '.jpg', '.jpeg'],
            'text/plain': ['.txt']
        },
        disabled: isLoading
    });

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!file) {
            setError('Please select a file');
            return;
        }
        if (accessType === 'specific' && !recipientEmail) {
            setError('Please enter recipient email');
            return;
        }

        setIsLoading(true);
        setError('');
        setProgress(0);

        try {
            let symmetricKey = null;
            let encryptedAesKey = null;

            // 1. Генерация ключа шифрования (если нужно)
            if (accessType === 'private' || accessType === 'specific') {
                setProgress(5);
                symmetricKey = await generateAesKey();
            }

            // 2. Шифрование ключа (если нужно)
            if (accessType === 'private' || accessType === 'specific') {
                setProgress(15);
                const publicKeyPem = localStorage.getItem('public_key');
                if (!publicKeyPem) throw new Error('Public key not found in localStorage');
                encryptedAesKey = await encryptWithPublicKey(symmetricKey, publicKeyPem);
            }

            // 3. Шифрование файла (если нужно)
            setProgress(30);
            const fileToUpload = accessType === 'private' || accessType === 'specific'
                ? await encryptFile(file, symmetricKey)
                : file;

            // 4. Подготовка FormData для отправки
            setProgress(50);
            const formData = new FormData();
            formData.append('file', fileToUpload, file.name); // Третий параметр - имя файла
            formData.append('name', file.name);

            if (accessType === 'private' || accessType === 'specific') {
                formData.append('symmetric_key', encryptedAesKey);
            }

            console.log(111111, formData.get('symmetric_key'))
            // 5. Отправка на сервер с прогрессом
            setProgress(75);
            const response = await filesApi.createFile(formData, (progressEvent) => {
                const percentCompleted = Math.round(
                    (progressEvent.loaded * 100) / progressEvent.total
                );
                setProgress(75 + percentCompleted * 0.25);
            });

            // 6. Делимся файлом (если нужно)
            if (accessType === 'specific') {
                encryptedAesKey = await encryptWithPublicKey(symmetricKey, recipientPublicKey);
                await filesApi.shareFile(response.file.uuid, {
                    recipient_uuid: recipientUuid,
                    symmetric_key: encryptedAesKey,
                });
            }

            setProgress(100);
            navigate('/storage/my');
        } catch (err) {
            console.error('File upload error:', err);
            setError(err.message || 'Failed to upload file');
            setProgress(0);
        } finally {
            setIsLoading(false);
        }
    };

    const readFileAsBase64 = (file) => {
        return new Promise((resolve, reject) => {
            const reader = new FileReader();
            reader.onload = () => {
                const base64Data = reader.result.split(',')[1];
                resolve(base64Data);
            };
            reader.onerror = reject;
            reader.readAsDataURL(file);
        });
    };

    const removeFile = () => {
        setFile(null);
        setProgress(0);
    };

    return (
        <div className="upload-page">
            <div className="upload-container">
                <h2 className="upload-title">Upload File</h2>
                <p className="upload-description">
                    {accessType === 'private'
                        ? 'Files are encrypted before uploading. Only you can decrypt them.'
                        : accessType === 'public'
                            ? 'Files will be accessible to anyone with the link.'
                            : 'Files will be encrypted for the specific recipient.'}
                </p>

                <div
                    {...getRootProps()}
                    className={`dropzone ${isDragActive ? 'active' : ''} ${file ? 'has-file' : ''}`}
                >
                    <input {...getInputProps()} />
                    {file ? (
                        <div className="file-preview">
                            <div className="file-icon">
                                {file.type.startsWith('image/') ? (
                                    <i className="icon-image"></i>
                                ) : (
                                    <i className="icon-file"></i>
                                )}
                            </div>
                            <div className="file-info">
                                <p className="file-name">{file.name}</p>
                                <p className="file-size">{(file.size / 1024 / 1024).toFixed(2)} MB</p>
                            </div>
                            <button
                                type="button"
                                className="remove-btn"
                                onClick={(e) => {
                                    e.stopPropagation();
                                    removeFile();
                                }}
                                disabled={isLoading}
                            >
                                &times;
                            </button>
                        </div>
                    ) : (
                        <>
                            <i className="icon-upload"></i>
                            <p className="dropzone-text">
                                {isDragActive ? 'Drop the file here' : 'Drag & drop a file, or click to select'}
                            </p>
                            <p className="dropzone-hint">Supports: PDF, JPG, PNG, TXT (Max 50MB)</p>
                        </>
                    )}
                </div>

                <div className="access-controls">
                    <div className="access-options">
                        <label className="access-option">
                            <input
                                type="radio"
                                name="accessType"
                                value="private"
                                checked={accessType === 'private'}
                                onChange={() => setAccessType('private')}
                            />
                            <span className="access-label">
                <i className="icon-lock"></i> Private (only me)
              </span>
                        </label>

                        <label className="access-option">
                            <input
                                type="radio"
                                name="accessType"
                                value="public"
                                checked={accessType === 'public'}
                                onChange={() => setAccessType('public')}
                            />
                            <span className="access-label">
                <i className="icon-globe"></i> Public (anyone with link)
              </span>
                        </label>

                        <label className="access-option">
                            <input
                                type="radio"
                                name="accessType"
                                value="specific"
                                checked={accessType === 'specific'}
                                onChange={() => setAccessType('specific')}
                            />
                            <span className="access-label">
                <i className="icon-user"></i> Specific person
              </span>
                        </label>
                    </div>

                    {accessType === 'specific' && (
                        <div className="recipient-section">
                            <label>Select recipient:</label>
                            <UserSelector
                                onUserSelect={(user) => {
                                    if (user) {
                                        setRecipientEmail(user.email);
                                        setRecipientPublicKey(user.public_key);
                                        setRecipientUuid(user.uuid);
                                    } else {
                                        setRecipientEmail('');
                                        setRecipientPublicKey(null);
                                        setRecipientUuid(null);
                                    }
                                }}
                            />
                        </div>
                    )}
                </div>

                {progress > 0 && progress < 100 && (
                    <div className="progress-bar">
                        <div
                            className="progress-fill"
                            style={{ width: `${progress}%` }}
                        ></div>
                        <span className="progress-text">{Math.round(progress)}%</span>
                    </div>
                )}

                {error && (
                    <div className="error-message">
                        <i className="icon-error"></i>
                        <span>{error}</span>
                    </div>
                )}

                <div className="actions">
                    <button
                        type="button"
                        className="cancel-btn"
                        onClick={() => navigate('/storage/my')}
                        disabled={isLoading}
                    >
                        Cancel
                    </button>
                    <button
                        type="submit"
                        className="upload-btn"
                        onClick={handleSubmit}
                        disabled={!file || isLoading || (accessType === 'specific' && !recipientEmail)}
                    >
                        {isLoading ? (
                            <>
                                <i className="icon-spinner"></i>
                                {accessType === 'private' ? 'Encrypting & Uploading...' : 'Uploading...'}
                            </>
                        ) : (
                            accessType === 'private' ? 'Encrypt & Upload' : 'Upload File'
                        )}
                    </button>
                </div>
            </div>
        </div>
    );
}
