import { useState, useEffect } from 'react';
import Spinner from "./Spinner";
import filesApi from "../api/files";
import UserSelector from "./UserSelector";

export default function FileItem({ file, onDownload, onDelete, isOwner, isDownloading }) {
    const [showShareModal, setShowShareModal] = useState(false);
    const [email, setEmail] = useState('');
    const [shareError, setShareError] = useState(null);
    const [sharedUsers, setSharedUsers] = useState([]);
    const [loadingSharedUsers, setLoadingSharedUsers] = useState(false);
    const [recipientEmail, setRecipientEmail] = useState('');
    const [recipientPublicKey, setRecipientPublicKey] = useState(null);
    const [recipientUuid, setRecipientUuid] = useState(null);

    useEffect(() => {
        if (showShareModal && isOwner) {
            fetchSharedUsers();
        }
    }, [isOwner, showShareModal]);

    const fetchSharedUsers = async () => {
        setLoadingSharedUsers(true);
        try {
            // Здесь должен быть API-запрос для получения списка пользователей
            // const response = await filesApi.getSharedUsers(file.id);
            // setSharedUsers(response.data.users);
        } catch (error) {
            console.error("Failed to fetch shared users:", error);
        } finally {
            setLoadingSharedUsers(false);
        }
    };

    const handleShareSubmit = async (e) => {
        e.preventDefault();
        if (!email) return;

        setShareError(null);

        try {
            setEmail('');

            await fetchSharedUsers();
        } catch (error) {
            setShareError(error.message || "Failed to share file");
        }
    };

    const handleRevokeAccess = async (userId) => {
        try {
            await filesApi.revokeAccess(file.id, userId);
            // Обновляем список после отзыва доступа
            await fetchSharedUsers();
        } catch (error) {
            console.error("Failed to revoke access:", error);
            setShareError("Failed to revoke access");
        }
    };

    return (
        <>
            <div className="file-item">
                <div className="file-info">
                    <span className="file-name">{file.name}</span>
                    <div className="file-meta">
                        <span className="file-size">{file.size}</span>
                        <span className="file-date">{file.date}</span>
                    </div>
                </div>
                <div className="file-actions">
                    <button
                        className="download-btn"
                        onClick={() => onDownload(file.id)}
                        disabled={isDownloading}
                    >
                        {isDownloading ? (
                            <>
                                <Spinner size="small" color="white" />
                                Downloading...
                            </>
                        ) : 'Download'}
                    </button>
                    {isOwner && (
                        <>
                            <button
                                className="share-btn"
                                onClick={() => {
                                    setShowShareModal(true)
                                }}
                            >
                                Share
                            </button>
                            <button
                                className="delete-btn"
                                onClick={() => onDelete(file.id)}
                            >
                                Delete
                            </button>
                        </>
                    )}
                </div>
            </div>

            {/* Share Modal */}
            {showShareModal && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <button
                            className="modal-close"
                            onClick={() => setShowShareModal(false)}
                        >
                            &times;
                        </button>

                        <h3>Manage Access for "{file.name}"</h3>

                        <form onSubmit={handleShareSubmit} className="share-form">
                            <div className="form-group">
                                <div className="recipient-section">
                                    <label>Select recipient:</label>
                                    <UserSelector
                                        onUserSelect={(user) => {
                                            if (user) {
                                                setRecipientEmail(user.email);
                                                setRecipientPublicKey(user.public_key);
                                                setRecipientUuid(user.uuid);
                                            } else {
                                                setRecipientEmail('');
                                                setRecipientPublicKey(null);
                                                setRecipientUuid(null);
                                            }
                                        }}
                                    />
                                </div>
                            </div>
                        </form>

                        <div className="shared-users-list">
                            <h4>Users with access</h4>
                            {loadingSharedUsers ? (
                                <Spinner size="medium" />
                            ) : sharedUsers.length > 0 ? (
                                <ul>
                                    {sharedUsers.map(user => (
                                        <li key={user.id} className="shared-user-item">
                                            <span>{user.email}</span>
                                            <button
                                                onClick={() => handleRevokeAccess(user.id)}
                                                className="btn-revoke"
                                                title="Revoke access"
                                            >
                                                &times;
                                            </button>
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p className="no-users-message">No users have access yet</p>
                            )}
                        </div>

                        {shareError && <div className="error-message">{shareError}</div>}
                    </div>
                </div>
            )}
        </>
    );
}
