import { useState, useEffect } from 'react';
import FileItem from '../components/FileItem';

export default function MyFiles() {
    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Загрузка файлов пользователя
        const fetchFiles = async () => {
            try {
                // Здесь будет запрос к API
                const mockFiles = [
                    { id: 1, name: 'Документ1.pdf', size: '2.4 MB', date: '2023-05-15' },
                    { id: 2, name: 'Изображение.jpg', size: '1.2 MB', date: '2023-05-10' },
                ];
                setFiles(mockFiles);
            } catch (error) {
                console.error('Ошибка загрузки файлов:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchFiles();
    }, []);

    if (loading) return <div>Загрузка...</div>;

    return (
        <div className="files-list">
            {files.length > 0 ? (
                files.map((file) => (
                    <FileItem key={file.id} file={file} isOwner={true} />
                ))
            ) : (
                <p>У вас пока нет файлов</p>
            )}
        </div>
    );
}
